mapper.select:
SELECT * 
FROM (
	SELECT /*INDEX_DESC(A POST_PK)*/ ROWNUM AS no, A.* 
	FROM (
		SELECT COUNT(*) OVER () AS total, post.* 
		FROM post 

			AND writer_id = #{writerId}
			AND title like '%' || #{title} || '%'
			AND content like '%' || #{content} || '%'

	) A
	WHERE ROWNUM  <=  #{endNo}
)
WHERE no  >=  #{startNo}

mapper.update:
UPDATE post
title = #{title}, content = #{content}, updated = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
WHERE post_id = #{id}
